import React, { Component } from 'react';
import HeaderComponent from './Header';
import './NotesPage.scss';

const notesList = [
    {
        title: 'abc',
        body: 'abchhdhdbfbfhdhdfdf jfdjhfd fdhjhdjf dhjdhfjd djhfd'
    },
    {
        title: 'abc',
