import React, { Component } from 'react';
import './LoginPage.scss';

class LoginComponent extends Component {
    state = {
        username: '',
        password: '',
        errorUsername: false,
        errorPassword: false,
        errorLoginBox: ''
    }

    handleInputChange = (event) => { // Handle on change event of username and password field.
        const previousStateValues = this.state;
        previousStateValues[event.target.name] = event.target.value;

        this.setState({ previousStateValues });
    }

    onLoginClick = () => { // On click login button to validate username and password for procced to app login.
        if (this.state.username && this.state.password) { // Check username and password valid values.
        }
        else {
            let { errorUsername, errorPassword } = this.state;
            if (!this.state.username)
                errorUsername = true;

            if (!this.state.password)
                errorPassword = true;

            this.setState({
                ...this.state,
                errorUsername,
                errorPassword
            })
        }
    }

    render() {
        return (
            <div className="login-root">

                <div className="row login-field-box">
                    <div className="col-12">
                        <div>
                            <label className="label-cs">Username</label>
                            <label className="label-cs">*</label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Username"
                                name="username"
                                onChange={this.handleInputChange}
                            />
                            {this.state.errorUsername && <div>Username is required</div>}
                        </div>

                        <div>
                            <label className="label-cs">Password</label>
                            <label className="label-cs">*</label>
                            <input
                                type="password"
                                className="form-control"
                                placeholder="Password"
                                name="password"
                                onChange={this.handleInputChange}
                            />
                            {this.state.errorPassword && <div>Password is required</div>}
                        </div>

                        <div>
                            <button
                                className="btn btn-block login-btn"
                                type="submit"
                                onClick={this.onLoginClick}>Login</button>
                        </div>
                    </div>
                </div>

            </div>
        );
    }
}

export default LoginComponent;
